/*
import random
import contextlib


def add(x, y):
    return x + y


def sub(x, y):
    return x - y


def mul(x, y):
    return x * y


def cDiv(x, y):
    neg = (x < 0) != (y < 0)
    if x < 0:
        x = -x
    if y < 0:
        y = -y
    ans = x // y
    return -ans if neg else ans


def cMod(x, y):
    neg = x < 0
    if x < 0:
        x = -x
    if y < 0:
        y = -y
    ans = x % y
    return -ans if neg else ans


def getNum(lo, high):
    x = random.randint(lo, high)
    return (random.randint(2 ** ((x - 1) * 32), 2 ** (x * 32)), x)


def genLine():
    op = random.choice(("+", "-", "*", "/", "%"))
    xNeg = random.choice((True, False))
    yNeg = random.choice((True, False))
    if op in {"/", "%"}:
        (x, sz) = getNum(1, 100)
        y = getNum(sz + 1, 101)[0]
    else:
        x = getNum(1, 100)[0]
        y = getNum(1, 100)[0]
    if xNeg:
        x = -x
    if yNeg:
        y = -y
    z = {"+": add, "-": sub, "*": mul, "/": cDiv, "%": cMod}[op](x, y)
    return f"""EXPECT_TRUE(BigInt("{x}") {op} BigInt("{y}") == BigInt("{z}"));"""


def toStdOut(count):
    for _ in range(count):
        print(genLine())


def toFile(count):
    with open("generated.txt", "w") as file:
        with contextlib.redirect_stdout(file):
            toStdOut(count)


toFile(100)
 */

